  newComment: string = '';

  likeVideo(video: Video) {
    // Logic to handle liking a video
    this.dashBoardService.addLikes(video.id).subscribe(
      (response) =>
      {
        console.log('success');
          this.getVideos();
      },
      (error) => {
        console.log('failure');
      })

    }


  commentVideo(video: Video) {
    // Logic to handle commenting on a video
   
    console.log('Commenting on video:', video.title);
  
  }

  addComment(video: Video) {
    if (this.newComment.trim() !== '') {
      this.isAddSpinerLoading = true;
      const newCommentObj: any = {
        id: video.comments.length + 1,
        text: this.newComment,
        author: 'Milind',
        avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcStsz-ePYChlKmmDp0G6-3nUZOJQ3d8KmY7DA&s',
        video_id: video.id
      };
      this.dashBoardService.addComment(newCommentObj, video.id).subscribe(
        (response) =>{
          console.log('success');
          this.getVideos();
          this.isAddSpinerLoading = false;
        },
        (error) => {
          this.isAddSpinerLoading = false;
        }
        
      );
      this.showCommentSection = false;
      this.newComment = '';
    }
  }

  evaluateComment(comment: Comment) {
    // Implement the logic for evaluating a comment
    comment.isLoading = true;
    this.commentEvaluationService.evaluateComment(comment).subscribe(
         (isPositive) => {
            comment.sentiment = isPositive ? 'positive' : 'negative';
            comment.isLoading = false;
          },
          (error) =>
          {
            comment.isLoading = false;
          });
  }
